input [16:1] A, B;
output [16:1] S;
output C_out;
wire [3:1] c_out;
cla_4bit cla1 (.A(A[4:1]), .B(B[4:1]), .cin(0), .S(S[4:1]), .cout(c_out[1]));
cla_4bit cla2 (.A(A[8:5]), .B(B[8:5]), .cin(c_out[1]), .S(S[8:5]), .cout(c_out[2]));
cla_4bit cla3 (.A(A[12:9]), .B(B[12:9]), .cin(c_out[2]), .S(S[12:9]), .cout(c_out[3]));
cla_4bit cla4 (.A(A[16:13]), .B(B[16:13]), .cin(c_out[3]), .S(S[16:13]), .cout(C_out));
endmodule
module cla_4bit(A, B, cin, S, cout);
input [4:1] A, B;
input cin;
output [4:1] S;
output cout;
wire [4:1] p, g;
wire [3:1] c_out;
genvar i;
generate
for(i=1; i<=4; i=i+1) begin:pg
    assign p[i] = A[i] ^ B[i];
    assign g[i] = A[i] & B[i];
end
endgenerate
assign c_out[1] = g[1] | (p[1] & cin);
assign c_out[2] = g[2] | (p[2] & g[1]) | (p[2] & p[1] & cin);
assign c_out[3] = g[3] | (p[3] & g[2]) | (p[3] & p[2] & g[1]) | (p[3] & p[2] & p[1] & cin);
assign cout = g[4] | (p[4] & g[3]) | (p[4] & p[3] & g[2]) | (p[4] & p[3] & p[2] & g[1]) | (p[4] & p[3] & p[2] & p[1] & cin);
assign S[1] = p[1] ^ cin;
assign S[2] = p[2] ^ c_out[1];
assign S[3] = p[3] ^ c_out[2];
assign S[4] = p[4] ^ c_out[3];
endmodule

// This is the top module for the carry lookahead adder
// It instantiates several 4-bit carry lookahead adders
// It takes 16 bits of input A and B and outputs a 16-bit sum S and a carry C_out
`timescale 1ns/1ps
module add_16bit(A, B, S, C_out);
    input [16:1] A, B;
    output [16:1] S;
    output C_out;
    wire [16:1] carry;
    cla_4bit cla1(.A(A[4:1]), .B(B[4:1]), .Cin(carry[1]), .S(S[4:1]), .Cout(carry[2]));
    cla_4bit cla2(.A(A[8:5]), .B(B[8:5]), .Cin(carry[6]), .S(S[8:5]), .Cout(carry[7]));
    cla_4bit cla3(.A(A[12:9]), .B(B[12:9]), .Cin(carry[10]), .S(S[12:9]), .Cout(carry[11]));
    cla_4bit cla4(.A(A[16:13]), .B(B[16:13]), .Cin(carry[14]), .S(S[16:13]), .Cout(carry[15]));
    assign C_out = carry[16];
endmodule
// This is the 4-bit carry lookahead adder block
module cla_4bit(A, B, Cin, S, Cout);
    input [4:1] A, B;
    input Cin;
    output [4:1] S;
    output Cout;
    wire [4:1] carry;
    genvar i;
    generate
        for (i = 1; i <= 4; i = i + 1) begin: gen_loop
            if (i == 1)
                cla_unit cla1(.A(A[i]), .B(B[i]), .Cin(Cin), .S(S[i]), .Cout(carry[i]));
            else
                cla_unit cla2(.A(A[i]), .B(B[i]), .Cin(carry[i - 1]), .S(S[i]), .Cout(carry[i]));
        end
    endgenerate
    assign Cout = carry[4];
endmodule
// This is the carry lookahead unit
module cla_unit(A, B, Cin, S, Cout);
    input A, B, Cin;
    output S, Cout;
    wire G, P;
    and gate_and(G, A, B);
    xor prop_xor(P, A, B);
    assign S = P ^ Cin;
    assign Cout = G | (P & Cin);
endmodule

// This is the top module for the carry lookahead 16-bit adder
`timescale 1ns/1ps
module add_16bit(A, B, S, C_out);
    input [16:1] A, B;
    output [16:1] S;
    output C_out;
    wire [3:1] c_in;
    cla_4bit cla1(.A(A[4:1]), .B(B[4:1]), .C_in(c_in[1]), .S(S[4:1]), .C_out(c_in[2]));
    cla_4bit cla2(.A(A[8:5]), .B(B[8:5]), .C_in(c_in[2]), .S(S[8:5]), .C_out(c_in[3]));
    cla_4bit cla3(.A(A[12:9]), .B(B[12:9]), .C_in(c_in[3]), .S(S[12:9]), .C_out(c_in[4]));
    cla_4bit cla4(.A(A[16:13]), .B(B[16:13]), .C_in(c_in[4]), .S(S[16:13]), .C_out(C_out));
endmodule
// 4-bit carry lookahead adder block
module cla_4bit(A, B, C_in, S, C_out);
    input [4:1] A, B;
    input C_in;
    output [4:1] S;
    output C_out;
    wire [4:1] p, g;
    wire [4:1] c;
    assign p = A ^ B;
    assign g = A & B;
    assign c[1] = g[1] | (p[1] & C_in);
    assign c[2] = g[2] | (p[2] & c[1]);
    assign c[3] = g[3] | (p[3] & c[2]);
    assign c[4] = g[4] | (p[4] & c[3]);
    assign S = p ^ c;
    assign C_out = c[4];
endmodule

module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);
    wire [1:0] state;
    wire [4:0] wave_inst;
    state_inst inst_state(.clk(clk), .rst_n(rst_n), .state(state));
    wave_inst inst_wave(.clk(clk), .rst_n(rst_n), .state(state), .wave(wave_inst));
    assign wave = (state == 2'b00) ? wave_inst : ~wave_inst;
endmodule
module state_inst (
    input clk,
    input rst_n,
    output reg [1:0] state
);
    always @(posedge clk) begin
        if (~rst_n) begin
            state <= 2'b00;
        end else begin
            state <= state + 1;
        end
    end
endmodule
module wave_inst (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);
    always @(posedge clk) begin
        if (~rst_n) begin
            wave <= 5'b00000;
        end else begin
            case (state)
                2'b00: wave <= wave + 1;
                2'b01: wave <= wave - 1;
                default: wave <= wave;
            endcase
        end
    end
endmodule

