wire [16:1] C;
cla_4bit add1(A[4:1], B[4:1], 1'b0, S[4:1], C[1]);
cla_4bit add2(A[8:5], B[8:5], C[1], S[8:5], C[2]);
cla_4bit add3(A[12:9], B[12:9], C[2], S[12:9], C[3]);
cla_4bit add4(A[16:13], B[16:13], C[3], S[16:13], C_out);
endmodule
module cla_4bit(A, B, Cin, S, Cout);
input [4:1] A, B;
input Cin;
output [4:1] S;
output Cout;
wire [4:1] P, G;
wire [4:1] C;
assign P = A ^ B;
assign G = A & B;
assign C[1] = G[1] | (P[1] & Cin);
assign C[2] = G[2] | (P[2] & C[1]);
assign C[3] = G[3] | (P[3] & C[2]);
assign C[4] = G[4] | (P[4] & C[3]);
assign Cout = C[4];
assign S = P ^ C;
endmodule

module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);
    wire [1:0] state;
    wire [4:0] wave_inc, wave_dec;
    state_inst state_inst(.clk(clk), .rst_n(rst_n), .state(state));
    wave_inst wave_inst(.clk(clk), .rst_n(rst_n), .state(state), .wave(wave_inc));
    wave_inst wave_inst(.clk(clk), .rst_n(rst_n), .state(state), .wave(wave_dec));
    mux_inst mux_inst(.state(state), .wave_inc(wave_inc), .wave_dec(wave_dec), .wave(wave));
endmodule
module state_inst (
    input clk,
    input rst_n,
    output reg [1:0] state
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= 2'b00;
        end else begin
            state <= {state[0], ~state[0]};
        end
    end
endmodule
module wave_inst (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave <= 5'b0;
        end else begin
            case (state)
                2'b00: wave <= wave + 5'b1;
                2'b01: wave <= wave - 5'b1;
                default: wave <= wave;
            endcase
        end
    end
endmodule
module mux_inst (
    input [1:0] state,
