// using Mentor Graphics HDL Designer(TM) 2012.1 (Build 6)
// 
// using Mentor Graphics HDL Designer(TM) 2012.1 (Build 6)
// 
// This model represents a Triangle Wave Generator
// 
// This model is used to generate a waveform that is incremented and decremented
// The wave is defined as a 5-bit register wave
// 
// The state is defined as a 2-bit register state
// 
// The state is used to control the waveform generation
// 
// The wave is generated by incrementing and decrementing the wave
// 
// The wave is reset to 0 on reset
// 
// The wave generation continues to cycle between 0 and 31 based on the state transitions
// 
`resetall
`timescale 1ns/10ps
module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);
    wire [4:0] state_inst_wave;
    wire [4:0] wave_inst_wave;
    state_inst state_inst (
        .clk(clk),
        .rst_n(rst_n),
        .wave(state_inst_wave)
    );
    wave_inst wave_inst (
        .clk(clk),
        .rst_n(rst_n),
        .wave(wave_inst_wave)
    );
    assign wave = state_inst_wave | wave_inst_wave;
endmodule // signal_generator
module state_inst (
    input clk,
    input rst_n,
    output [4:0] wave
);
    reg [1:0] state;
    assign wave = state[1] ? 5'b00000 : 5'b10000;
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            state <= 2'b00;
        end
        else begin
            state <= state + 1'b1;
        end
    end
endmodule // state_inst
module wave_inst (
    input clk,
    input rst_n,
    output [4:0] wave
);
    reg [4:0] wave_reg;
    assign wave = wave_reg;
    always @(posedge clk or negedge rst_n) begin
        if (~rst_n) begin
            wave_reg <= 5'b00000;
        end
        else begin
            if (wave_reg == 5'b11111) begin
                wave_reg <= 5'b00000;
            end
            else begin
                wave_reg <= wave_reg + 1'b1;
            end
        end
    end
endmodule // wave_inst

module signal_generator (
    input clk,
    input rst_n,
    output [4:0] wave
);
    wire [1:0] state;
    state_inst state_inst(.clk(clk), .rst_n(rst_n), .state(state));
    wave_inst wave_inst(.clk(clk), .rst_n(rst_n), .state(state), .wave(wave));
endmodule
module state_inst (
    input clk,
    input rst_n,
    output reg [1:0] state
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= 2'b00;
        end else begin
            state <= state + 1;
        end
    end
endmodule
module wave_inst (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave <= 5'b00000;
        end else begin
            case (state)
                2'b00: wave <= wave + 1;
                2'b01: wave <= wave - 1;
            endcase
        end
    end
endmodule

