module state_inst (
    input clk,
    input rst_n,
    output reg [1:0] state
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            state <= 2'b00;
        end else begin
            state <= {state[0], ~state[1]};
        end
    end
