module wave_inst (
    input clk,
    input rst_n,
    input [1:0] state,
    output reg [4:0] wave
);
    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            wave <= 5'b00000;
        end else begin
            if (state == 2'b00) begin
                if (wave == 5'b11111) begin
                    wave <= 5'b00000;
                end else begin
                    wave <= wave + 1'b1;
                end
            end else if (state == 2'b01) begin
                if (wave == 5'b00000) begin
                    wave <= 5'b11111;
                end else begin
                    wave <= wave - 1'b1;
                end
            end
        end
    end
