module parity_16bit(X, Y, P);
  // declare I/Os
  input [7:0] X, Y;
  output P;
  wire [7:0] P_int;
  // instantiate parity generator modules
  parity_gen p1(X[7:0], P_int[0]);
  parity_gen p2(Y[7:0], P_int[1]);
