module parity_gen(P_int, P);
  input [7:0] P_int;
  output P;
  wire [7:0] gen1, gen2;
  // instantiate half adder modules
  half_adder ha1(P_int[0], P_int[1], gen1[0], gen2[0]);
  half_adder ha2(P_int[2], P_int[3], gen1[1], gen2[1]);
  half_adder ha3(P_int[4], P_int[5], gen1[2], gen2[2]);
  half_adder ha4(P_int[6], P_int[7], gen1[3], gen2[3]);
  half_adder ha5(gen1[0], gen1[1], gen1[2], gen1[3]);
  half_adder ha6(gen1[1], gen1[2], gen1[3], gen1[4]);
  half_adder ha7(gen1[2], gen1[3], gen1[4], gen1[5]);
  half_adder ha8(gen1[3], gen1[4], gen1[5], gen1[6]);
  half_adder ha9(gen1[4], gen1[5], gen1[6], gen1[7]);
  half_adder ha10(gen2[0], gen2[1], gen2[2], gen2[3]);
  half_adder ha11(gen2[1], gen2[2], gen2[3], gen2[4]);
  half_adder ha12(gen2[2], gen2[3], gen2[4], gen2[5]);
  half_adder ha13(gen2[3], gen2[4], gen2[5], gen2[6]);
  half_adder ha14(gen2[4], gen2[5], gen2[6], gen2[7]);
  // instantiate final parity computation module
  parity_computation pc(gen1, gen2, P);
endmodule
module parity_computation(gen1, gen2, P);
  input [7:0] gen1, gen2;
  output P;
  xor xor1(P, gen1[0], gen2[0]);
endmodule
module half_adder(a, b, sum, carry);
  input a, b;
