module add_16bit(A, B, S, C_out);
input [16:1] A, B;
output [16:1] S;
output C_out;
wire [16:1] C;
four_bit fa1(.A(A[4]), .B(B[4]), .C_in(0), .S(S[4]), .C_out(C[4]));
four_bit fa2(.A(A[8]), .B(B[8]), .C_in(C[4]), .S(S[8]), .C_out(C[8]));
four_bit fa3(.A(A[12]), .B(B[12]), .C_in(C[8]), .S(S[12]), .C_out(C[12]));
four_bit fa4(.A(A[16]), .B(B[16]), .C_in(C[12]), .S(S[16]), .C_out(C_out));
endmodule
module four_bit(A, B, C_in, S, C_out);
input [3:0] A, B;
input C_in;
output [3:0] S;
output C_out;
wire [3:0] P, G;
wire [3:1] C;
assign P = A ^ B;
assign G = A & B;
assign S = P ^ C;
assign C_out = G | (P & C);
full_adder fa1(.A(A[0]), .B(B[0]), .C_in(C_in), .S(S[0]), .C_out(C[1]));
full_adder fa2(.A(A[1]), .B(B[1]), .C_in(C[1]), .S(S[1]), .C_out(C[2]));
full_adder fa3(.A(A[2]), .B(B[2]), .C_in(C[2]), .S(S[2]), .C_out(C[3]));
full_adder fa4(.A(A[3]), .B(B[3]), .C_in(C[3]), .S(S[3]), .C_out(C_out));
endmodule
module full_adder(A, B, C_in, S, C_out);
input A, B, C_in;
output S, C_out;
assign S = A ^ B ^ C_in;
assign C_out = (A & B) | (A & C_in) | (B & C_in);
endmodule

