module add_16bit(A, B, S, C_out);
input [16:1] A, B;
output [16:1] S;
output C_out;
wire [16:1] C;
four_bit_fa fa1(.A(A[5:1]), .B(B[5:1]), .C_in(C[4]), .S(S[5:1]), .C_out(C[5]));
four_bit_fa fa2(.A(A[9:6]), .B(B[9:6]), .C_in(C[8]), .S(S[9:6]), .C_out(C[9]));
four_bit_fa fa3(.A(A[13:10]), .B(B[13:10]), .C_in(C[12]), .S(S[13:10]), .C_out(C[13]));
