//Carry-Lookahead Adder (CLA)
module cla_4bit(A, B, C_in, S, C_out);
input [4:1] A;
input [4:1] B;
input C_in;
output [4:1] S;
output C_out;
wire [4:1] P;
wire [4:1] G;
wire [4:1] C;
//propagate signals
assign P = A ^ B;
assign G = A & B;
//carry signals
assign C[1] = G[1] | (P[1] & C_in);
assign C[2] = G[2] | (P[2] & G[1]) | (P[2] & P[1] & C_in);
assign C[3] = G[3] | (P[3] & G[2]) | (P[3] & P[2] & G[1]) | (P[3] & P[2] & P[1] & C_in);
assign C[4] = G[4] | (P[4] & G[3]) | (P[4] & P[3] & G[2]) | (P[4] & P[3] & P[2] & G[1]) | (P[4] & P[3] & P[2] & P[1] & C_in);
//sum
assign S = P ^ C;
//carry-out
assign C_out = C[4];
endmodule

// This is the code for the 16-bit carry-lookahead adder
`timescale 1ns/1ps
module add_16bit(A, B, S, C_out);
input [16:1] A, B;
output [16:1] S;
output C_out;
wire [16:1] carry;
wire [16:1] G, P;
wire [16:1] C;
assign C[1] = 0;
genvar i;
generate for(i=1; i<16; i=i+1) begin: addbit
    if(i==16)
        assign carry[i] = C[i];
    else
        assign carry[i] = G[i] | (P[i] & C[i]);
end
endgenerate
