CLA_4bit cla3(.A(A[13:10]), .B(B[13:10]), .C_in(C[3]), .S(S[13:10]), .C_out(C[4]));
CLA_4bit cla4(.A(A[17:14]), .B(B[17:14]), .C_in(C[4]), .S(S[17:14]), .C_out(C[5]));
CLA_4bit cla5(.A(A[1:1]), .B(B[1:1]), .C_in(C[1]), .S(S[1:1]), .C_out(C_out));
endmodule
