full_adder fa1(.A(A[1]), .B(B[1]), .C_in(C[1]), .S(S[1]), .C_out(C[2]));
full_adder fa2(.A(A[2]), .B(B[2]), .C_in(C[2]), .S(S[2]), .C_out(C[3]));
