output [16:1] S;
output C_out;
wire [16:1] carry;
wire [16:1] sum;
wire [16:1] carry_out;
wire [16:1] carry_in;
genvar i;
generate
for(i=1; i<=16; i=i+1) begin: addbit
  if(i==1) begin: first
    cla_addbit first_addbit(.A(A[i]), .B(B[i]), .Cin(1'b0), .S(S[i]), .Cout(carry_out[i]));
  end
  else begin: other
    cla_addbit other_addbit(.A(A[i]), .B(B[i]), .Cin(carry_in[i-1]), .S(S[i]), .Cout(carry_out[i]));
  end
end
