module cla_addbit(A, B, Cin, S, Cout);
input A;
input B;
input Cin;
output S;
output Cout;
wire P;
wire G;
assign P = A ^ B;
assign G = A & B;
assign S = P ^ Cin;
assign Cout = G | (P & Cin);
endmodule

