module shift_reg (
    input clk,
    input d,
    output q
);
    wire q1, q2;
    my_dff u0(clk, d, q1);
    my_dff u1(clk, q1, q2);
    my_dff u2(clk, q2, q);
endmodule
module my_dff (
    input clk,
    input d,
    output reg q
);
    always @(posedge clk) begin
        q <= d;
    end
endmodule

