module add_16bit(A, B, S, C_out);
input [16:1] A, B;
output [16:1] S;
output C_out;
wire [16:1] C;
four_bit_full_adder fa1(A[4:1], B[4:1], 0, S[4:1], C[4:1]);
four_bit_full_adder fa2(A[8:5], B[8:5], C[4:1], S[8:5], C[8:5]);
four_bit_full_adder fa3(A[12:9], B[12:9], C[8:5], S[12:9], C[12:9]);
four_bit_full_adder fa4(A[16:13], B[16:13], C[12:9], S[16:13], C_out);
endmodule
module four_bit_full_adder(A, B, Cin, S, Cout);
input [4:1] A, B;
input Cin;
output [4:1] S;
output Cout;
wire [4:1] C;
one_bit_full_adder fa1(A[1], B[1], Cin, S[1], C[1]);
one_bit_full_adder fa2(A[2], B[2], C[1], S[2], C[2]);
one_bit_full_adder fa3(A[3], B[3], C[2], S[3], C[3]);
one_bit_full_adder fa4(A[4], B[4], C[3], S[4], Cout);
endmodule
module one_bit_full_adder(A, B, Cin, S, Cout);
input A, B, Cin;
output S, Cout;
wire P, G;
assign P = A ^ B;
assign G = A & B;
assign S = P ^ Cin;
assign Cout = G | (P & Cin);
endmodule

