module stagepipe5(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  input [31:0] reg_file [0:31],
  output [31:0] res_reg_file [0:31]
);
  wire [31:0] pc;
  wire [31:0] rs1;
  wire [31:0] rs2;
  wire [31:0] rd;
  wire [31:0] op;
  wire [31:0] alu_rs1;
  wire [31:0] alu_rs2;
  wire [31:0] alu_result;
  wire [31:0] mem_result;
  wire [31:0] writeback_result;
  fetch_stage fetch_stage(
    .clk(clk),
    .rst(rst),
    .instr_mem(instr_mem),
    .pc(pc),
    .instr(op)
  );
  decode_stage decode_stage(
    .clk(clk),
    .rst(rst),
    .instr(op),
    .rs1(rs1),
    .rs2(rs2),
    .rd(rd)
  );
  execute_stage execute_stage(
    .clk(clk),
    .rst(rst),
    .rs1(alu_rs1),
    .rs2(alu_rs2),
    .rd(rd),
    .op(op),
    .alu_result(alu_result)
  );
  memory_stage memory_stage(
    .clk(clk),
    .rst(rst),
    .alu_result(alu_result),
    .mem_result(mem_result)
  );
  writeback_stage writeback_stage(
    .clk(clk),
    .rst(rst),
    .rd(rd),
    .reg_file(reg_file),
    .writeback_result(writeback_result)
  );
  assign res_reg_file[0] = writeback_result;
  assign res_reg_file[1] = mem_result;
  assign res_reg_file[2] = alu_rs2;
  assign res_reg_file[3] = alu_rs1;
  assign res_reg_file[4] = op;
endmodule
module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc,
  output reg [31:0] instr
);
  always @(posedge clk) begin
    if (rst) begin
      pc <= 0;
      instr <= 0;
    end else begin
      instr <= instr_mem[pc];
      pc <= pc + 1;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr,
  output reg [31:0] rs1,
  output reg [31:0] rs2,
  output reg [31:0] rd
);
  always @(posedge clk) begin
    if (rst) begin
      rs1 <= 0;
      rs2 <= 0;
      rd <= 0;
    end else begin
      rs1 <= instr[19:15];
      rs2 <= instr[24:20];
      rd <= instr[11:7];
    end
  end
endmodule
module execute_stage(
  input clk,
  input rst,
  input [31:0] rs1,
  input [31:0] rs2,
  input [31:0] rd,
  input [31:0] op,
  output reg [31:0] alu_result
);
  always @(posedge clk) begin
    if (rst) begin
      alu_result <= 0;
    end else begin
      case (op)
        0: alu_result <= rs1 + rs2;
        1: alu_result <= rs1 - rs2;
        default: alu_result <= 0;
      endcase
    end
  end
endmodule
module memory_stage(
  input clk,
  input rst,
  input [31:0] alu_result,
  output reg [31:0] mem_result
);
  always @(posedge clk) begin
    if (rst) begin
      mem_result <= 0;
    end else begin
      mem_result <= alu_result;
    end
  end
endmodule
module writeback_stage(
  input clk,
  input rst,
  input [31:0] rd,
  input [31:0] reg_file [0:31],
  output reg [31:0] writeback_result
);
  always @(posedge clk) begin
    if (rst) begin
      writeback_result <= 0;
    end else begin
      writeback_result <= reg_file[rd];
    end
  end
