module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc,
  output reg [31:0] instr
);
  always @(posedge clk) begin
    if (rst) begin
      pc <= 0;
      instr <= 0;
    end else begin
      instr <= instr_mem[pc];
      pc <= pc + 1;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr,
  output reg [31:0] rs1,
  output reg [31:0] rs2,
  output reg [31:0] rd,
  output reg [31:0] op
);
  always @(posedge clk) begin
    if (rst) begin
      rs1 <= 0;
      rs2 <= 0;
      rd <= 0;
      op <= 0;
    end else begin
      rs1 <= instr[19:15];
      rs2 <= instr[24:20];
      rd <= instr[11:7];
      op <= instr[31:25];
    end
  end
endmodule
module execute_stage(
  input clk,
  input rst,
  input [31:0] rs1,
  input [31:0] rs2,
  input [31:0] op,
  output reg [31:0] alu_in,
  output reg [31:0] alu_out
);
  always @(posedge clk) begin
    if (rst) begin
      alu_in <= 0;
      alu_out <= 0;
    end else begin
      alu_in <= rs1 + rs2;
      alu_out <= rs1 - rs2;
    end
  end
endmodule
module memory_stage(
  input clk,
  input rst,
  input [31:0] alu_out,
  output reg [31:0] mem_in,
  output reg [31:0] mem_out
);
  always @(posedge clk) begin
    if (rst) begin
      mem_in <= 0;
      mem_out <= 0;
    end else begin
      mem_in <= alu_out;
      mem_out <= alu_out;
    end
  end
endmodule
