module stagepipe5(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  input [31:0] reg_file [0:31],
  output [31:0] res_reg_file [0:31]
);
wire [31:0] pc;
wire [31:0] instr;
wire [31:0] rs1;
wire [31:0] rs2;
wire [31:0] rd;
wire [31:0] op;
wire [31:0] alu_rs1;
wire [31:0] alu_rs2;
wire [31:0] alu_result;
wire [31:0] reg_file_wr_data;
wire [31:0] res_reg_file_wr_data;
fetch_stage fetch_stage(.clk(clk), .rst(rst), .instr_mem(instr_mem), .pc(pc), .instr(instr));
decode_stage decode_stage(.clk(clk), .rst(rst), .instr(instr), .rs1(rs1), .rs2(rs2), .rd(rd), .op(op));
execute_stage execute_stage(.clk(clk), .rst(rst), .rs1(alu_rs1), .rs2(alu_rs2), .op(op), .alu_result(alu_result));
memory_stage memory_stage(.clk(clk), .rst(rst), .alu_result(alu_result), .reg_file(reg_file), .rs1(rs1), .rs2(rs2), .rd(rd), .op(op), .reg_file_wr_data(reg_file_wr_data), .alu_rs1(alu_rs1), .alu_rs2(alu_rs2));
writeback_stage writeback_stage(.clk(clk), .rst(rst), .reg_file(reg_file), .reg_file_wr_data(reg_file_wr_data), .res_reg_file(res_reg_file), .rs1(rs1), .rs2(rs2), .rd(rd), .alu_rs1(alu_rs1), .alu_rs2(alu_rs2), .alu_result(alu_result), .res_reg_file_wr_data(res_reg_file_wr_data));
endmodule
module fetch_stage(
  input clk,
  input rst,
  input [31:0] instr_mem [0:31],
  output reg [31:0] pc,
  output reg [31:0] instr
);
  always @(posedge clk) begin
    if (rst) begin
      pc <= 0;
      instr <= 0;
    end else begin
      instr <= instr_mem[pc];
      pc <= pc + 1;
    end
  end
endmodule
module decode_stage(
  input clk,
  input rst,
  input [31:0] instr,
  output reg [31:0] rs1,
  output reg [31:0] rs2,
  output reg [31:0] rd,
  output reg [31:0] op
);
  always @(posedge clk) begin
    if (rst) begin
      rs1 <= 0;
      rs2 <= 0;
      rd <= 0;
      op <= 0;
    end else begin
      rs1 <= instr[19:15];
      rs2 <= instr[24:20];
      rd <= instr[11:7];
      op <= instr[31:25];
    end
  end
endmodule
module execute_stage(
  input clk,
  input rst,
  input [31:0] rs1,
  input [31:0] rs2,
  input [31:0] op,
  output reg [31:0] alu_rs1,
  output reg [31:0] alu_rs2,
  output reg [31:0] alu_result
);
  always @(posedge clk) begin
    if (rst) begin
      alu_rs1 <= 0;
      alu_rs2 <= 0;
      alu_result <= 0;
    end else begin
      alu_rs1 <= rs1;
      alu_rs2 <= rs2;
      alu_result <= op == 4 ? rs1 - rs2 : rs1 + rs2;
    end
  end
endmodule
module memory_stage(
  input clk,
  input rst,
  input [31:0] alu_result,
  input [31:0] reg_file [0:31],
  input [31:0] rs1,
  input [31:0] rs2,
  input [31:0] rd,
  input [31:0] op,
  output reg [31:0] reg_file_wr_data,
  output reg [31:0] alu_rs1,
  output reg [31:0] alu_rs2
);
  always @(posedge clk) begin
    if (rst) begin
      reg_file_wr_data <= 0;
      alu_rs1 <= 0;
      alu_rs2 <= 0;
    end else begin
      alu_rs1 <= reg_file[rs1];
      alu_rs2 <= reg_file[rs2];
      reg_file_wr_data <= op == 8 ? alu_result : reg_file[rd];
    end
  end
endmodule
module writeback_stage(
  input clk,
  input rst,
  input [31:0] reg_file [0:31],
  input [31:0] reg_file_wr_data,
