  output reg [31:0] res_reg_file_wr_data
);
  always @(posedge clk) begin
    if (rst) begin
      res_reg_file_wr_data <= 0;
      res_reg_file[0] <= 0;
      res_reg_file[1] <= 0;
      res_reg_file[2] <= 0;
      res_reg_file[3] <= 0;
      res_reg_file[4] <= 0;
      res_reg_file[5] <= 0;
      res_reg_file[6] <= 0;
      res_reg_file[7] <= 0;
      res_reg_file[8] <= 0;
      res_reg_file[9] <= 0;
      res_reg_file[10] <= 0;
      res_reg_file[11] <= 0;
      res_reg_file[12] <= 0;
      res_reg_file[13] <= 0;
      res_reg_file[14] <= 0;
      res_reg_file[15] <= 0;
      res_reg_file[16] <= 0;
      res_reg_file[17] <= 0;
      res_reg_file[18] <= 0;
      res_reg_file[19] <= 0;
      res_reg_file[20] <= 0;
      res_reg_file[21] <= 0;
      res_reg_file[22] <= 0;
      res_reg_file[23] <= 0;
      res_reg_file[24] <= 0;
      res_reg_file[25] <= 0;
      res_reg_file[26] <= 0;
      res_reg_file[27] <= 0;
      res_reg_file[28] <= 0;
      res_reg_file[29] <= 0;
      res_reg_file[30] <= 0;
      res_reg_file[31] <= 0;
    end else begin
      res_reg_file[rd] <= reg_file_wr_data;
      res_reg_file_wr_data <= alu_rs1 + alu_rs2;
    end
  end
endmodule

