// Full subtractor module
module full_subtractor(
    input a,
    input b,
    input bin,
    output diff,
    output bout);
    // Difference and borrow wires
    wire w0, w1, w2;
    // Subtract a and b
    assign diff = a ^ b ^ bin;
    // Borrow wires
    assign w0 = ~a & (b^bin) | b & bin;
    assign w1 = w0 & (~a^b) | b & ~a;
    assign w2 = w1 & (~a^b) | b & ~a;
    // Borrow output
    assign bout = w2 & (~a^b) | b & ~a;
endmodule

