    gen_partial_product GP6_i(.X(X[6:0]), .Y(Y), .P(GP6));
    gen_partial_product GP7_i(.X(X[7:0]), .Y(Y), .P(GP7));
    assign P = GP0 + (GP1 << 1) + (GP2 << 2) + (GP3 << 3) + (GP4 << 4) + (GP5 << 5) + (GP6 << 6) + (GP7 << 7);
