module gen_product(X, Y, P);
    input [7:0] X, Y;
    output [3:0] P;
    wire [3:0] carry;
    wire [3:0] partial_product;
    assign partial_product = X & {4{Y[0]}};
    assign carry[0] = partial_product[0] & 1'b1;
    assign carry[1] = partial_product[1] & 1'b1;
