module mult_8bit(X, Y, P);
	input [7:0] X;
	input [7:0] Y;
	output [15:0] P;
	wire [15:0] AS0_out, AS1_out, AS2_out, AS3_out, AS4_out, AS5_out, AS6_out, AS7_out;
	add_shift AS0(.X(X), .Y(Y), .shift(0), .acc(0), .out(AS0_out));
	add_shift AS1(.X(X), .Y(Y), .shift(1), .acc(AS0_out), .out(AS1_out));
	add_shift AS2(.X(X), .Y(Y), .shift(2), .acc(AS1_out), .out(AS2_out));
	add_shift AS3(.X(X), .Y(Y), .shift(3), .acc(AS2_out), .out(AS3_out));
	add_shift AS4(.X(X), .Y(Y), .shift(4), .acc(AS3_out), .out(AS4_out));
	add_shift AS5(.X(X), .Y(Y), .shift(5), .acc(AS4_out), .out(AS5_out));
	add_shift AS6(.X(X), .Y(Y), .shift(6), .acc(AS5_out), .out(AS6_out));
	add_shift AS7(.X(X), .Y(Y), .shift(7), .acc(AS6_out), .out(AS7_out));
	assign P = AS7_out;
endmodule
module add_shift(X, Y, shift, acc, out);
	input [7:0] X;
	input [7:0] Y;
	input [3:0] shift;
	input [15:0] acc;
	output [15:0] out;
	wire [15:0] X_shifted;
	assign X_shifted = X << shift;
	assign out = acc + (Y & X_shifted);
endmodule

