module mult_8bit
  (
       X,
       Y,
       P
  )
  ;
  input   [7:0] X;
  input   [7:0] Y;
  output  [15:0] P;
  //  Internal Wires
  wire    [15:0] P;
  //  Instantiate add_shift modules
  add_shift AS0
    (
         .X(X),
         .Y(Y),
         .shift(0),
         .P(P)
  );
  add_shift AS1
    (
         .X(X),
         .Y(Y),
         .shift(1),
         .P(P)
  );
  add_shift AS2
    (
         .X(X),
         .Y(Y),
         .shift(2),
         .P(P)
  );
  add_shift AS3
    (
         .X(X),
         .Y(Y),
         .shift(3),
         .P(P)
  );
  add_shift AS4
    (
         .X(X),
         .Y(Y),
         .shift(4),
         .P(P)
  );
  add_shift AS5
    (
         .X(X),
         .Y(Y),
         .shift(5),
         .P(P)
  );
  add_shift AS6
    (
         .X(X),
         .Y(Y),
         .shift(6),
         .P(P)
  );
  add_shift AS7
    (
         .X(X),
         .Y(Y),
         .shift(7),
         .P(P)
  );
  add_shift AS8
    (
         .X(X),
         .Y(Y),
         .shift(8),
         .P(P)
  );
  add_shift AS9
    (
         .X(X),
         .Y(Y),
         .shift(9),
         .P(P)
  );
  add_shift AS10
    (
         .X(X),
         .Y(Y),
         .shift(10),
         .P(P)
  );
endmodule
//----------------------------------------------------------------------------
//  This module performs a single addition/shift operation.  It is called
//  by the multiply_8bit module.
//----------------------------------------------------------------------------
module add_shift
  (
       X,
       Y,
       shift,
       P
  )
  ;
  input   [7:0] X;
  input   [7:0] Y;
  input   [3:0] shift;
  output  [15:0] P;
  //  Internal Wires
  wire    [15:0] P;
  //  Conditionally add multiplicand to accumulator
  assign P = (Y[shift] == 1'b1) ? (X << shift) : 16'h0000;
endmodule

