    add_shift AS0(.X(X), .Y(Y), .in(8'b0), .shift(Y[0]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS1(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[1]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS2(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[2]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS3(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[3]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS4(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[4]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS5(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[5]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS6(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[6]), .acc(P[15:8]), .out(P[7:0]));
    add_shift AS7(.X(X), .Y(Y), .in(P[7:0]), .shift(Y[7]), .acc(P[15:8]), .out(P[7:0]));
endmodule
module add_shift(X, Y, in, shift, acc, out);
    input [7:0] X, Y;
    input [7:0] in;
    input shift;
    output [7:0] out;
    output [7:0] acc;
    wire [7:0] out;
    wire [7:0] acc;
    assign acc = shift ? (X + Y) : (in + X + Y);
    assign out = shift ? (acc >> 1) : acc;
endmodule

