module mult_8bit(X, Y, P);
    input [7:0] X, Y;
    output [15:0] P;
    wire [15:0] P;
    add_shift AS0(.X(X), .Y(Y), .P(P), .shift(0));
    add_shift AS1(.X(X), .Y(Y), .P(P), .shift(1));
    add_shift AS2(.X(X), .Y(Y), .P(P), .shift(2));
    add_shift AS3(.X(X), .Y(Y), .P(P), .shift(3));
    add_shift AS4(.X(X), .Y(Y), .P(P), .shift(4));
    add_shift AS5(.X(X), .Y(Y), .P(P), .shift(5));
    add_shift AS6(.X(X), .Y(Y), .P(P), .shift(6));
    add_shift AS7(.X(X), .Y(Y), .P(P), .shift(7));
endmodule
module add_shift(X, Y, P, shift);
    input [7:0] X, Y;
    input [3:0] shift;
    output [15:0] P;
    wire [15:0] P;
    wire [7:0] X_shifted;
    assign X_shifted = X << shift;
    assign P = (Y[shift] == 1'b1) ? (P + X_shifted) : P;
endmodule

module mul_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [7:0] pp0, pp1, pp2, pp3;
    assign pp0 = (Y[0]) ? {4'b0000, X} : 8'b00000000;
    assign pp1 = (Y[1]) ? {3'b000, X, 1'b0} : 8'b00000000;
    assign pp2 = (Y[2]) ? {2'b00, X, 2'b00} : 8'b00000000;
    assign pp3 = (Y[3]) ? {1'b0, X, 3'b000} : 8'b00000000;
    adder_8bit add1(.A(pp0), .B(pp1), .Cin(1'b0), .S(P[7:0]), .Cout());
endmodule
module adder_8bit(A, B, Cin, S, Cout);
    input [7:0] A, B;
    input Cin;
    output [7:0] S;
    output Cout;
    wire C1, C2, C3, C4, C5, C6, C7;
    full_adder fa0(.A(A[0]), .B(B[0]), .Cin(Cin), .S(S[0]), .Cout(C1));
    full_adder fa1(.A(A[1]), .B(B[1]), .Cin(C1), .S(S[1]), .Cout(C2));
    full_adder fa2(.A(A[2]), .B(B[2]), .Cin(C2), .S(S[2]), .Cout(C3));
    full_adder fa3(.A(A[3]), .B(B[3]), .Cin(C3), .S(S[3]), .Cout(C4));
    full_adder fa4(.A(A[4]), .B(B[4]), .Cin(C4), .S(S[4]), .Cout(C5));
    full_adder fa5(.A(A[5]), .B(B[5]), .Cin(C5), .S(S[5]), .Cout(C6));
    full_adder fa6(.A(A[6]), .B(B[6]), .Cin(C6), .S(S[6]), .Cout(C7));
    full_adder fa7(.A(A[7]), .B(B[7]), .Cin(C7), .S(S[7]), .Cout(Cout));
endmodule
module full_adder(A, B, Cin, S, Cout);
    input A, B, Cin;
    output S, Cout;
    assign S = A ^ B ^ Cin;
    assign Cout = (A & B) | (B & Cin) | (A & Cin);
endmodule

module mul_4bit(X, Y, P);
    input [3:0] X, Y;
    output [7:0] P;
    wire [7:0] P;
    wire [7:0] partials;
    assign partials[0] = (Y[0] & X[0]);
    assign partials[1] = (Y[1] & X[1]);
    assign partials[2] = (Y[2] & X[2]);
    assign partials[3] = (Y[3] & X[3]);
    assign partials[4] = (Y[0] & X[1]) << 1;
    assign partials[5] = (Y[1] & X[2]) << 1;
    assign partials[6] = (Y[2] & X[3]) << 1;
    assign partials[7] = (Y[3] & X[3]) << 1;
    adder_8bit add1(.A(partials[0]), .B(partials[4]), .Cin(1'b0), .S(P[0]), .Cout());
    adder_8bit add2(.A(partials[1]), .B(partials[5]), .Cin(1'b0), .S(P[1]), .Cout());
    adder_8bit add3(.A(partials[2]), .B(partials[6]), .Cin(1'b0), .S(P[2]), .Cout());
    adder_8bit add4(.A(partials[3]), .B(partials[7]), .Cin(1'b0), .S(P[3]), .Cout());
    adder_8bit add5(.A(P[0]), .B(P[1]), .Cin(1'b0), .S(P[4]), .Cout());
    adder_8bit add6(.A(P[2]), .B(P[3]), .Cin(1'b0), .S(P[5]), .Cout());
    adder_8bit add7(.A(P[4]), .B(P[5]), .Cin(1'b0), .S(P[6]), .Cout());
    adder_8bit add8(.A(P[6]), .B(P[7]), .Cin(1'b0), .S(P[7]), .Cout());
endmodule
module adder_8bit(A, B, Cin, S, Cout);
    input [7:0] A, B;
    input Cin;
    output [7:0] S;
    output Cout;
    wire [7:0] carry;
    full_adder fa0(.A(A[0]), .B(B[0]), .Cin(Cin), .S(S[0]), .Cout(carry[0]));
    full_adder fa1(.A(A[1]), .B(B[1]), .Cin(carry[0]), .S(S[1]), .Cout(carry[1]));
    full_adder fa2(.A(A[2]), .B(B[2]), .Cin(carry[1]), .S(S[2]), .Cout(carry[2]));
    full_adder fa3(.A(A[3]), .B(B[3]), .Cin(carry[2]), .S(S[3]), .Cout(carry[3]));
    full_adder fa4(.A(A[4]), .B(B[4]), .Cin(carry[3]), .S(S[4]), .Cout(carry[4]));
    full_adder fa5(.A(A[5]), .B(B[5]), .Cin(carry[4]), .S(S[5]), .Cout(carry[5]));
    full_adder fa6(.A(A[6]), .B(B[6]), .Cin(carry[5]), .S(S[6]), .Cout(carry[6]));
    full_adder fa7(.A(A[7]), .B(B[7]), .Cin(carry[6]), .S(S[7]), .Cout(Cout));
endmodule
module full_adder(A, B, Cin, S, Cout);
    input A, B, Cin;
    output S, Cout;
    assign S = A ^ B ^ Cin;
    assign Cout = (A & B) | (B & Cin) | (A & Cin);
endmodule

module pwm_controller(
    input clk,
    input [7:0] duty_cycle,
    output pwm_out
);
wire [7:0] adjusted_duty;
pulse_gen pulse_gen(.clk(clk), .duty_cycle(adjusted_duty), .pwm_out(pwm_out));
duty_ctrl duty_ctrl(.clk(clk), .duty_cycle(duty_cycle), .adjusted_duty(adjusted_duty));
endmodule
module pulse_gen(
    input clk,
    input [7:0] duty_cycle,
    output pwm_out
);
reg [7:0] internal_counter = 0;
reg pwm_out_reg = 0;
assign pwm_out = pwm_out_reg;
always @(posedge clk) begin
    if (internal_counter < duty_cycle) begin
        pwm_out_reg <= 1;
    end else begin
        pwm_out_reg <= 0;
    end
    internal_counter <= internal_counter + 1;
end
endmodule
