module JC_counter(
    input                clk,
    input                rst_n,
    output [63:0]       Q
);
// Instantiate register and logic modules
reg_inst reg_inst(.clk(clk), .rst_n(rst_n), .Q(Q));
logic_inst logic_inst(.clk(clk), .rst_n(rst_n), .Q(Q));
endmodule
module reg_inst(
    input                clk,
    input                rst_n,
    output [63:0]       Q
);
// Register to store the current state of the counter
reg [63:0] Q_reg;
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        Q_reg <= 64'b0;
    end
    else begin
        Q_reg <= Q;
    end
end
assign Q = Q_reg;
endmodule
module logic_inst(
    input                clk,
    input                rst_n,
    output [63:0]       Q
);
// Intermediate wires
wire [63:0] Q_next;
// Calculate the next state of the counter
assign Q_next = {Q[62:0], ~Q[63]};
// Update the counter state
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        Q <= 64'b0;
    end
    else begin
        Q <= Q_next;
    end
end
endmodule

