    output reg [31:0] odd
);
reg [31:0] quotient;
reg [31:0] remainder;
reg [31:0] divisor;
reg [5:0] count;
always @(*) begin
    divisor = B_16;
    remainder = A_32;
    quotient = 32'b0;
    count = 6'b0;
