    remainder = 0;
    quotient = 0;
    for (i = 0; i < 32; i = i + 1) begin
        remainder = remainder << 1;
        remainder[0] = A_32_copy[31];
        A_32_copy = A_32_copy << 1;
        if (remainder >= B_16_copy) begin
            remainder = remainder - B_16_copy;
            quotient = quotient << 1;
            quotient[0] = 1;
        end
        else begin
            quotient = quotient << 1;
            quotient[0] = 0;
        end
    end
    result = quotient;
    odd = remainder;
end
endmodule

