        if (rst) begin
            div_result <= 16'b0;
        end else begin
            div_result <= div_result_next;
        end
    end
    // Sequential logic for updating the arithmetic operation result
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            result <= 16'b0;
        end else begin
            result <= result_next;
        end
    end
    // Sequential logic for updating the valid flag
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            valid <= 1'b0;
        end else begin
            valid <= valid_next;
        end
    end
    // Output assignment
    assign add_cout_final = add_cout;
    assign sub_cout_final = sub_cout;
    assign mul_cout_final = mul_cout;
    assign div_cout_final = div_cout;
