    assign mul_result_final_next = mul_result_next;
    assign div_result_final_next = div_result_next;
    assign add_result_final_final = add_result_final;
    assign sub_result_final_final = sub_result_final;
    assign mul_result_final_final = mul_result_final;
    assign div_result_final_final = div_result_final;
    assign result_final = result;
    assign valid_final = valid;
endmodule
// Adder module
module adder(
    input [7:0] a,
    input [7:0] b,
    input cin,
    output [8:0] cout,
    output [8:0] result
);
    assign {cout, result} = a + b + cin;
endmodule
// Subtractor module
module subtractor(
    input [7:0] a,
    input [7:0] b,
    input cin,
    output [8:0] cout,
    output [8:0] result
);
    assign {cout, result} = a - b - cin;
endmodule
// Multiplier module
module multiplier(
    input [7:0] a,
    input [7:0] b,
    input cin,
    output [8:0] cout,
    output [15:0] result
);
    assign {cout, result} = a * b + cin;
endmodule
// Divider module
module divider(
    input [7:0] a,
    input [7:0] b,
    input cin,
    output [8:0] cout,
    output [15:0] result
);
    assign {cout, result} = a / b + cin;
    // When division by zero is attempted, set the valid flag to 0
    assign valid = ~b[0];
endmodule

