    wire [15:0] sub_result;
    wire [15:0] mul_result;
    wire [15:0] div_result;
    wire [15:0] add_result_next;
    wire [15:0] sub_result_next;
    wire [15:0] mul_result_next;
    wire [15:0] div_result_next;
    add_sub_stage add_module(
        .clk(clk),
        .rst(rst),
