        .clk(clk),
        .rst(rst),
        .data_a(data_a),
        .data_b(data_b),
        .cout(div_cout),
        .result(div_out)
    );
    always @(posedge clk) begin
        if (rst) begin
            add_cout <= 0;
            sub_cout <= 0;
            mul_cout <= 0;
            div_cout <= 0;
        end else begin
            add_cout <= add_cout_next;
            sub_cout <= sub_cout_next;
            mul_cout <= mul_cout_next;
            div_cout <= div_cout_next;
        end
    end
    always @(*) begin
        if (opcode == 2'b00) begin
            add_cout_next = add_cout;
            sub_cout_next = sub_cout;
            mul_cout_next = mul_cout;
            div_cout_next = div_cout;
        end else if (opcode == 2'b01) begin
            add_cout_next = sub_cout;
            sub_cout_next = add_cout;
            mul_cout_next = mul_cout;
            div_cout_next = div_cout;
        end else if (opcode == 2'b10) begin
            add_cout_next = mul_cout;
            sub_cout_next = mul_cout;
            mul_cout_next = add_cout;
            div_cout_next = div_cout;
        end else begin
            add_cout_next = div_cout;
            sub_cout_next = div_cout;
            mul_cout_next = div_cout;
            div_cout_next = add_cout;
        end
    end
    always @(*) begin
        if (opcode == 2'b00) begin
            add_result_next = {add_cout, add_out};
            sub_result_next = {sub_cout, sub_out};
            mul_result_next = mul_out;
            div_result_next = div_out;
        end else if (opcode == 2'b01) begin
            add_result_next = {sub_cout, sub_out};
            sub_result_next = {add_cout, add_out};
            mul_result_next = mul_out;
            div_result_next = div_out;
        end else if (opcode == 2'b10) begin
            add_result_next = mul_out;
            sub_result_next = mul_out;
            mul_result_next = {add_cout, add_out};
            div_result_next = div_out;
        end else begin
            add_result_next = div_out;
            sub_result_next = div_out;
            mul_result_next = mul_out;
            div_result_next = {add_cout, add_out};
        end
    end
    always @(posedge clk) begin
        if (rst) begin
            result <= 0;
            valid <= 1;
        end else begin
            result <= add_result_next;
            valid <= valid;
        end
    end
    always @(*) begin
        if (div_out == 0) begin
            valid = 0;
        end else begin
            valid = 1;
        end
    end
endmodule
