module div_ctrl (
    input wire [15:0] A,
    input wire [7:0] B,
    output reg [15:0] q,
    output reg [15:0] r
); ASSISTANT:  // 
    reg [15:0] temp_q;
    reg [15:0] temp_r;
    reg [15:0] temp_d;
    reg [4:0] count;
    always @(*) begin
        temp_q = 0;
        temp_r = A;
        temp_d = {8'b0, B};
        count = 5'b10000;
        while (count) begin
            temp_r = temp_r << 1;
            if (temp_r >= temp_d) begin
                temp_r = temp_r - temp_d;
                temp_q = temp_q << 1;
                temp_q[0] = 1;
            end
            count = count - 1;
        end
        q = temp_q;
        r = temp_r[15:0];
    end
endmodule

