// control unit
module control (
    input wire [15:0] A,
    input wire [7:0] B,
    output reg [15:0] result,
    output reg [15:0] odd
); ASSISTANT:  // 
    reg [15:0] temp_quotient;
    reg [15:0] temp_remainder;
    reg [15:0] temp_dividend;
    reg [15:0] temp_divisor;
    reg [3:0] count;
    always @(*) begin
        // Initialize temporary variables
        temp_quotient = 0;
        temp_remainder = 0;
        temp_dividend = A;
        temp_divisor = B;
        count = 4'b1000;
        // Perform division operation
        while (count) begin
            // Shift dividend
            temp_remainder = temp_remainder << 1;
            temp_remainder[0] = temp_dividend[15];
            temp_dividend = temp_dividend << 1;
            // Compare dividend with divisor
            if (temp_remainder >= temp_divisor) begin
                temp_remainder = temp_remainder - temp_divisor;
                temp_quotient = temp_quotient << 1;
                temp_quotient[0] = 1;
            end
            count = count - 1;
        end
        // Update quotient and remainder
        result = temp_quotient;
        odd = temp_remainder;
    end
endmodule

// 
    wire [15:0] temp;
    div_ctrl u_div_ctrl (
        .A(A),
        .B(B),
        .result(result),
        .odd(odd),
        .temp(temp)
    );
endmodule
module div_ctrl (
    input wire [15:0] A,
    input wire [7:0] B,
    output reg [15:0] result,
    output reg [15:0] odd,
    output reg [15:0] temp
); ASSISTANT:  // 
    reg [7:0] divisor;
    reg [15:0] dividend;
    reg [3:0] count;
    always @(*) begin
        divisor = B;
        dividend = A;
        count = 4'b1000;
        result = 16'b0;
        odd = 16'b0;
        temp = 16'b0;
        while (count) begin
            if (dividend[15:8] >= divisor) begin
                dividend = dividend - divisor;
                temp = temp << 1;
                temp[0] = 1'b1;
            end else begin
                temp = temp << 1;
                temp[0] = 1'b0;
            end
            count = count - 1;
        end
        result = temp;
        odd = dividend;
    end
endmodule

module sub_8bit(A, B, D, B_out);
	input [7:0] A;
	input [7:0] B;
	output [7:0] D;
	output B_out;
	wire [7:0] B_comp;
	wire [7:0] carry;
	wire [7:0] sum;
	assign B_comp = ~B;
	assign B_out = carry[7];
	adder_8bit adder_inst(.A(A), .B(B_comp), .Cin(1'b1), .Cout(carry), .S(sum));
	assign D = B_comp^sum;
endmodule
module adder_8bit(A, B, Cin, Cout, S);
	input [7:0] A;
	input [7:0] B;
	input Cin;
	output [7:0] Cout;
	output [7:0] S;
	wire [6:0] carry;
	full_adder fa0(.a(A[0]), .b(B[0]), .cin(Cin), .cout(carry[0]), .sum(S[0]));
	full_adder fa1(.a(A[1]), .b(B[1]), .cin(carry[0]), .cout(carry[1]), .sum(S[1]));
	full_adder fa2(.a(A[2]), .b(B[2]), .cin(carry[1]), .cout(carry[2]), .sum(S[2]));
	full_adder fa3(.a(A[3]), .b(B[3]), .cin(carry[2]), .cout(carry[3]), .sum(S[3]));
	full_adder fa4(.a(A[4]), .b(B[4]), .cin(carry[3]), .cout(carry[4]), .sum(S[4]));
	full_adder fa5(.a(A[5]), .b(B[5]), .cin(carry[4]), .cout(carry[5]), .sum(S[5]));
	full_adder fa6(.a(A[6]), .b(B[6]), .cin(carry[5]), .cout(carry[6]), .sum(S[6]));
	full_adder fa7(.a(A[7]), .b(B[7]), .cin(carry[6]), .cout(Cout[7]), .sum(S[7]));
	assign Cout[6:0] = carry[6:0];
endmodule
module full_adder(a, b, cin, cout, sum);
	input a, b, cin;
	output cout, sum;
	assign sum = a^b^cin;
	assign cout = (a&b) | (b&cin) | (cin&a);
endmodule

module sub_8bit(A, B, D, B_out);
// INPUTS
input [7:0] A;
input [7:0] B;
// OUTPUTS
output [7:0] D;
output B_out;
// WIRES
wire [7:0] B_comp; // 2's complement of B
wire [7:0] carry; // Carry out from each full_adder
// Generate 2's complement of B
genvar i;
generate
for (i = 0; i < 8; i = i + 1) begin
    not(B_comp[i], B[i]);
end
endgenerate
// Perform subtraction
adder_8bit sub_adder(A, B_comp, D, B_out, carry);
endmodule
module adder_8bit(A, B, S, C_out, carry); ASSISTANT: 
// INPUTS
input [7:0] A;
input [7:0] B;
// OUTPUTS
output [7:0] S;
output C_out;
// WIRES
wire [7:0] carry;
// Add each bit
generate
genvar i;
for (i = 0; i < 8; i = i + 1) begin
    full_adder fa(A[i], B[i], carry[i], S[i], carry[i+1]);
end
endgenerate
assign C_out = carry[7];
endmodule
module full_adder(A, B, C_in, S, C_out); ASSISTANT: 
// INPUTS
input A;
input B;
input C_in;
// OUTPUTS
output S;
output C_out;
// LOGIC
assign S = A ^ B ^ C_in;
assign C_out = (A & B) | (B & C_in) | (C_in & A);
endmodule

