module sub_16bit(A, B, D, B_out);
    input [16:1] A, B;
    output [16:1] D;
    output B_out;
    wire [16:1] B_temp;
    wire [16:1] B_out_temp;
    genvar i;
    generate
        for(i=1; i<=16; i=i+1) begin: sub_loop
            if(i==1) begin: sub_first
                sub_4bit sub1(A[4:1], B[4:1], B_temp[4:1], B_out_temp[1]);
            end
            else begin: sub_other
                sub_4bit sub2(A[i+3:i], B[i+3:i], B_temp[i+3:i], B_out_temp[i]);
            end
        end
    endgenerate
    assign B_out = B_out_temp[16];
    assign D = B_temp;
