        for(i=1; i<=4; i=i+1) begin: sub_loop
            if(i==1) begin: sub_first
                full_subtractor sub1(A[1], B[1], B_temp[1], B_out_temp[1]);
            end
            else begin: sub_other
                full_subtractor sub2(A[i+1], B[i+1], B_temp[i+1], B_out_temp[i]);
            end
        end
    endgenerate
    assign B_out = B_out_temp[4];
    assign D = B_temp;
