                      (aluc == 6'b000110) ? (a[31] ^ b[31] ^ srlv_result[31] ^ srav_result[31]) :
                      (aluc == 6'b000111) ? (a[31] ^ b[31] ^ srlv_result[31] ^ srav_result[31]) :
                      (aluc == 6'b001111) ? (a[31] ^ b[31] ^ lui_result[31] ^ 1'b0) : 1'b0;
    assign flag = (aluc == 6'b101010) ? (slt_result[31] ^ slt_result[30]) :
                  (aluc == 6'b101011) ? (sltu_result[31] ^ sltu_result[30]) :
                  1'b0;
endmodule
module add_inst(
    input [31:0] a,
    input [31:0] b,
    input cin,
    output cout,
    output [31:0] result
);
    assign {cout, result} = a + b + cin;
endmodule
module addu_inst(
    input [31:0] a,
    input [31:0] b,
    input cin,
    output cout,
    output [31:0] result
);
    assign {cout, result} = a + b + cin;
endmodule
module sub_inst(
    input [31:0] a,
    input [31:0] b,
    input cin,
    output cout,
    output [31:0] result
);
    assign {cout, result} = a - b - cin;
endmodule
module subu_inst(
    input [31:0] a,
    input [31:0] b,
    input cin,
    output cout,
    output [31:0] result
);
    assign {cout, result} = a - b - cin;
endmodule
module and_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = a & b;
endmodule
module or_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = a | b;
endmodule
module xor_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = a ^ b;
endmodule
module nor_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = ~(a | b);
endmodule
module slt_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = {31'b0, a < b};
endmodule
module sltu_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = {31'b0, a < b};
endmodule
module sll_inst(
    input [31:0] a,
    input [4:0] b,
    output [31:0] result
);
    assign result = a << b;
endmodule
module srl_inst(
    input [31:0] a,
    input [4:0] b,
    output [31:0] result
);
    assign result = a >> b;
endmodule
module sra_inst(
    input [31:0] a,
    input [4:0] b,
    output [31:0] result
);
    assign result = $signed(a) >>> b;
endmodule
module sllv_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = a << b[4:0];
endmodule
module srlv_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = a >> b[4:0];
endmodule
module srav_inst(
    input [31:0] a,
    input [31:0] b,
    output [31:0] result
);
    assign result = $signed(a) >>> b[4:0];
endmodule
module lui_inst(
    input [31:0] a,
    input [15:0] imm,
    output [31:0] result
);
    assign result = {imm, a[15:0]};
endmodule

