module alu(
    input [31:0] a,
    input [31:0] b,
    input [5:0] aluc,
    output [31:0] r,
    output zero,
    output carry,
    output negative,
    output overflow,
    output flag
);
    wire [31:0] addu_res, subu_res, and_res, or_res, xor_res, nor_res, slt_res, sltu_res, sll_res, srl_res, sra_res, sllv_res, srlv_res, srav_res, lui_res;
    addsub addu_inst(a, b, 1'b0, addu_res, carry);
    addsub subu_inst(a, ~b, 1'b1, subu_res, carry);
    and_inst and_inst(a, b, and_res);
    or_inst or_inst(a, b, or_res);
    xor_inst xor_inst(a, b, xor_res);
    nor_inst nor_inst(a, b, nor_res);
    slt_inst slt_inst(a, b, slt_res);
    sltu_inst sltu_inst(a, b, sltu_res);
    sll sll_inst(a, b[4:0], sll_res);
    srl srl_inst(a, b[4:0], srl_res);
    sra sra_inst(a, b[4:0], sra_res);
    sllv sllv_inst(a, b, sllv_res);
    srlv srlv_inst(a, b, srlv_res);
    srav srav_inst(a, b, srav_res);
    lui_inst lui_inst(a, lui_res);
    assign r = (aluc == 6'b100000) ? addu_res :
               (aluc == 6'b100001) ? addu_res :
               (aluc == 6'b100010) ? subu_res :
               (aluc == 6'b100011) ? subu_res :
               (aluc == 6'b100100) ? and_res :
               (aluc == 6'b100101) ? or_res :
               (aluc == 6'b100110) ? xor_res :
               (aluc == 6'b100111) ? nor_res :
               (aluc == 6'b101010) ? slt_res :
               (aluc == 6'b101011) ? sltu_res :
               (aluc == 6'b000000) ? sll_res :
               (aluc == 6'b000010) ? srl_res :
               (aluc == 6'b000011) ? sra_res :
               (aluc == 6'b000100) ? sllv_res :
               (aluc == 6'b000110) ? srlv_res :
               (aluc == 6'b000111) ? srav_res :
               (aluc == 6'b001111) ? lui_res :
               32'b0;
    assign zero = (r == 32'b0);
    assign negative = r[31];
    assign overflow = (a[31] & b[31] & ~r[31]) | (~a[31] & ~b[31] & r[31]);
    assign flag = (aluc == 6'b101010) ? slt_res[31] :
                  (aluc == 6'b101011) ? sltu_res[31] :
                  1'b0;
endmodule
module addsub(a, b, carry_in, sum, carry_out);
    input [31:0] a, b;
    input carry_in;
    output [31:0] sum;
    output carry_out;
    wire [32:0] sum_ext;
    assign sum_ext = {1'b0, a} + {1'b0, b} + carry_in;
    assign sum = sum_ext[31:0];
    assign carry_out = sum_ext[32];
endmodule
module and_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = a & b;
endmodule
module or_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = a | b;
endmodule
module xor_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = a ^ b;
endmodule
module nor_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = ~(a | b);
endmodule
module slt_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = a[31] ^ b[31] ? a[31] : a < b;
endmodule
module sltu_inst(a, b, res);
    input [31:0] a, b;
    output [31:0] res;
    assign res = a < b;
endmodule
module sll(a, shamt, res);
    input [31:0] a;
    input [4:0] shamt;
    output [31:0] res;
    assign res = a << shamt;
endmodule
module srl(a, shamt, res);
    input [31:0] a;
    input [4:0] shamt;
    output [31:0] res;
    assign res = a >> shamt;
endmodule
module sra(a, shamt, res);
    input [31:0] a;
    input [4:0] shamt;
    output [31:0] res;
    assign res = $signed(a) >>> shamt;
endmodule
module sllv(a, b, res);
    input [31:0] a;
    input [31:0] b;
    output [31:0] res;
    assign res = a << b[4:0];
endmodule
module srlv(a, b, res);
    input [31:0] a;
    input [31:0] b;
    output [31:0] res;
    assign res = a >> b[4:0];
endmodule
module srav(a, b, res);
    input [31:0] a;
    input [31:0] b;
    output [31:0] res;
    assign res = $signed(a) >>> b[4:0];
endmodule
module lui_inst(a, res);
    input [31:0] a;
    output [31:0] res;
    assign res = {a[15:0], 16'b0};
endmodule

//Borrow-lookahead 16-bit subtractor
module sub_16bit(A, B, D, B_out);
input [16:1] A, B;
output [16:1] D;
output B_out;
wire [16:1] B_out_wire;
wire [16:1] B_in_wire;
assign B_in_wire = ~B;
bls_4bit sub_4bit_1(A[4:1], B_in_wire[4:1], B_out_wire[1], D[4:1], B_out_wire[4]);
bls_4bit sub_4bit_2(A[8:5], B_in_wire[8:5], B_out_wire[2], D[8:5], B_out_wire[8]);
bls_4bit sub_4bit_3(A[12:9], B_in_wire[12:9], B_out_wire[3], D[12:9], B_out_wire[12]);
bls_4bit sub_4bit_4(A[16:13], B_in_wire[16:13], B_out_wire[4], D[16:13], B_out_wire[16]);
assign B_out = B_out_wire[16];
endmodule
//Borrow-lookahead 4-bit subtractor
module bls_4bit(A, B, B_in, D, B_out);
input [4:1] A, B;
input B_in;
output [4:1] D;
output B_out;
wire [4:1] B_out_wire;
wire [4:1] B_in_wire;
assign B_in_wire = {B_in, B_in, B_in, B_in, B_in};
full_subtractor fsub_1(A[1], B[1], B_in_wire[1], D[1], B_out_wire[1]);
full_subtractor fsub_2(A[2], B[2], B_in_wire[2], D[2], B_out_wire[2]);
full_subtractor fsub_3(A[3], B[3], B_in_wire[3], D[3], B_out_wire[3]);
full_subtractor fsub_4(A[4], B[4], B_in_wire[4], D[4], B_out_wire[4]);
assign B_out = B_out_wire[4];
endmodule
//Full subtractor
module full_subtractor(A, B, B_in, D, B_out);
input A, B, B_in;
output D, B_out;
assign D = A ^ B ^ B_in;
assign B_out = (~A & B) | (B & B_in) | (~A & B_in);
endmodule

