//Borrow-lookahead 16-bit subtractor
`timescale 1ns/1ps
module sub_16bit(A, B, D, B_out);
    input [16:1] A, B;
    output [16:1] D;
    output B_out;
    wire [16:1] B_temp;
    wire [16:1] B_out_temp;
    genvar i;
    generate
        for(i = 1; i <= 16; i = i + 1) begin: sub_loop
            if(i == 1) begin: first_sub
                bls_4bit first_sub(A[i], B[i], 1'b1, D[i], B_temp[i], B_out_temp[i]);
            end
            else begin: other_sub
                bls_4bit other_sub(A[i], B[i], B_temp[i-1], D[i], B_temp[i], B_out_temp[i]);
            end
        end
    endgenerate
    assign B_out = B_out_temp[16];
endmodule
module bls_4bit(A, B, B_in, D, B_out, B_t);
    input A, B, B_in;
    output D, B_out, B_t;
    wire [3:0] D_temp;
    wire [3:0] B_temp;
    full_subtractor f1(A, B, B_in, D_temp[0], B_temp[0]);
    full_subtractor f2(A, B, B_in, D_temp[1], B_temp[1]);
    full_subtractor f3(A, B, B_in, D_temp[2], B_temp[2]);
    full_subtractor f4(A, B, B_in, D_temp[3], B_temp[3]);
    assign D = {D_temp[3], D_temp[2], D_temp[1], D_temp[0]};
    assign B_out = B_temp[3];
    assign B_t = B_temp[0];
endmodule
module full_subtractor(A, B, B_in, D, B_out);
    input A, B, B_in;
    output D, B_out;
    wire D_temp;
    wire B_temp;
    assign D_temp = A ^ B ^ B_in;
    assign B_temp = (~A & B) | (B & B_in) | (~A & B_in);
    assign D = D_temp;
    assign B_out = B_temp;
endmodule

module comparator_32bit(
    input [31:0] A,   // First 32-bit input operand
    input [31:0] B,   // Second 32-bit input operand
    output A_greater, 
    output A_equal,   
    output A_less     
);
    wire [31:0] A_minus_B;
    subtractor_32bit sub_inst(
        .A(A),
        .B(B),
        .res(A_minus_B)
    );
    assign A_greater = (|A_minus_B);
    assign A_equal = (A == B);
    assign A_less = ~(|A_minus_B) & ~(A == B);
endmodule
module subtractor_32bit(
    input [31:0] A,
    input [31:0] B,
    output [31:0] res
);
    assign res = A - B;
endmodule

