module adder_32bit(A,B,S,C32);
  input [32:1] A;
  input [32:1] B;
  output [32:1] S;
  output C32;
  wire [16:1] C16;
  CLA1 CLA11(.A(A[17:1]), .B(B[17:1]), .CIN(1'b0), .S(S[17:1]), .COUT(C16[1]));
  CLA1 CLA12(.A(A[18:1]), .B(B[18:1]), .CIN(C16[1]), .S(S[18:1]), .COUT(C16[2]));
  CLA1 CLA13(.A(A[19:1]), .B(B[19:1]), .CIN(C16[2]), .S(S[19:1]), .COUT(C16[3]));
  CLA1 CLA14(.A(A[20:1]), .B(B[20:1]), .CIN(C16[3]), .S(S[20:1]), .COUT(C16[4]));
  CLA1 CLA15(.A(A[21:1]), .B(B[21:1]), .CIN(C16[4]), .S(S[21:1]), .COUT(C16[5]));
  CLA1 CLA16(.A(A[22:1]), .B(B[22:1]), .CIN(C16[5]), .S(S[22:1]), .COUT(C16[6]));
  CLA1 CLA17(.A(A[23:1]), .B(B[23:1]), .CIN(C16[6]), .S(S[23:1]), .COUT(C16[7]));
  CLA1 CLA18(.A(A[24:1]), .B(B[24:1]), .CIN(C16[7]), .S(S[24:1]), .COUT(C16[8]));
  CLA1 CLA19(.A(A[25:1]), .B(B[25:1]), .CIN(C16[8]), .S(S[25:1]), .COUT(C16[9]));
  CLA1 CLA20(.A(A[26:1]), .B(B[26:1]), .CIN(C16[9]), .S(S[26:1]), .COUT(C16[10]));
  CLA1 CLA21(.A(A[27:1]), .B(B[27:1]), .CIN(C16[10]), .S(S[27:1]), .COUT(C16[11]));
  CLA1 CLA22(.A(A[28:1]), .B(B[28:1]), .CIN(C16[11]), .S(S[28:1]), .COUT(C16[12]));
  CLA1 CLA23(.A(A[29:1]), .B(B[29:1]), .CIN(C16[12]), .S(S[29:1]), .COUT(C16[13]));
  CLA1 CLA24(.A(A[30:1]), .B(B[30:1]), .CIN(C16[13]), .S(S[30:1]), .COUT(C16[14]));
  CLA1 CLA25(.A(A[31:1]), .B(B[31:1]), .CIN(C16[14]), .S(S[31:1]), .COUT(C16[15]));
  CLA2 CLA26(.A(A[32:1]), .B(B[32:1]), .CIN(C16[15]), .S(S[32:1]), .COUT(C32));
endmodule
module CLA1(A,B,CIN,S,COUT); ASSISTANT: 

 module CLA1(A,B,CIN,S,COUT);
  input [16:1] A;
  input [16:1] B;
  input CIN;
  output [16:1] S;
  output COUT;
  wire [16:1] G;
  wire [16:1] P;
  wire [16:1] C;
  genvar i;
  generate
    for(i=1;i<=16;i=i+1) begin
      GENI GENI1(.A(A[i]), .B(B[i]), .CIN(CIN), .GEN(G[i]), .PRO(P[i]));
      CARRY CARRY1(.CIN(C[i-1]), .GEN(G[i]), .PRO(P[i]), .COUT(C[i]));
      MUX MUX1(.A(A[i]), .B(B[i]), .GEN(G[i]), .PRO(P[i]), .S(S[i]));
    end
  endgenerate
  assign COUT = C[16];
endmodule
module CLA2(A,B,CIN,S,COUT); ASSISTANT: 

 module CLA2(A,B,CIN,S,COUT);
  input [16:1] A;
  input [16:1] B;
  input CIN;
  output [16:1] S;
  output COUT;
  wire [16:1] G;
  wire [16:1] P;
  wire [16:1] C;
  genvar i;
  generate
    for(i=1;i<=16;i=i+1) begin
      GENI GENI2(.A(A[i]), .B(B[i]), .CIN(CIN), .GEN(G[i]), .PRO(P[i]));
      CARRY CARRY2(.CIN(C[i-1]), .GEN(G[i]), .PRO(P[i]), .COUT(C[i]));
      MUX MUX2(.A(A[i]), .B(B[i]), .GEN(G[i]), .PRO(P[i]), .S(S[i]));
    end
  endgenerate
  assign COUT = C[16];
endmodule
module GENI(A,B,CIN,GEN,PRO); ASSISTANT: 

 module GENI(A,B,CIN,GEN,PRO);
  input A;
  input B;
  input CIN;
  output GEN;
  output PRO;
  assign GEN = A & B;
  assign PRO = A | B;
endmodule
module MUX(A,B,GEN,PRO,S); ASSISTANT: 

 module MUX(A,B,GEN,PRO,S);
  input A;
  input B;
  input GEN;
  input PRO;
  output S;
  assign S = (GEN & CIN) | (PRO & ~CIN);
endmodule
module CARRY(CIN,GEN,PRO,COUT); ASSISTANT: 

 module CARRY(CIN,GEN,PRO,COUT);
  input CIN;
  input GEN;
  input PRO;
  output COUT;
  assign COUT = GEN | (PRO & CIN);
endmodule

module adder_32bit(A,B,S,C32);
	input [32:1] A,B;
	output [32:1] S;
	output C32;
	wire [15:1] C16;
	CLA16 CLA1(.A(A[16:1]), .B(B[16:1]), .CIN(1'b0), .S(S[16:1]), .COUT(C16[1]));
	CLA16 CLA2(.A(A[32:17]), .B(B[32:17]), .CIN(C16[1]), .S(S[32:17]), .COUT(C32));
endmodule
module CLA16(A,B,CIN,S,COUT); ASSISTANT: 
	input [15:1] A,B;
	input CIN;
	output [15:1] S;
	output COUT;
	wire [14:1] C;
	CLA4 CLA1(.A(A[4:1]), .B(B[4:1]), .CIN(CIN), .S(S[4:1]), .COUT(C[1]));
	CLA4 CLA2(.A(A[8:5]), .B(B[8:5]), .CIN(C[1]), .S(S[8:5]), .COUT(C[2]));
	CLA4 CLA3(.A(A[12:9]), .B(B[12:9]), .CIN(C[2]), .S(S[12:9]), .COUT(C[3]));
	CLA4 CLA4(.A(A[16:13]), .B(B[16:13]), .CIN(C[3]), .S(S[16:13]), .COUT(COUT));
endmodule
module CLA4(A,B,CIN,S,COUT); ASSISTANT: 
	input [3:1] A,B;
	input CIN;
	output [3:1] S;
	output COUT;
	wire [2:1] C;
	CLA1 CLA1(.A(A[1:0]), .B(B[1:0]), .CIN(CIN), .S(S[1:0]), .COUT(C[1]));
	CLA1 CLA2(.A(A[2:1]), .B(B[2:1]), .CIN(C[1]), .S(S[2:1]), .COUT(C[2]));
	CLA1 CLA3(.A(A[3:2]), .B(B[3:2]), .CIN(C[2]), .S(S[3:2]), .COUT(COUT));
endmodule
module CLA1(A,B,CIN,S,COUT); ASSISTANT: 
	input [1:0] A,B;
	input CIN;
	output [1:0] S;
	output COUT;
	wire [1:0] C;
	CLA CLA1(.A(A[0]), .B(B[0]), .CIN(CIN), .S(S[0]), .COUT(C[0]));
	CLA CLA2(.A(A[1]), .B(B[1]), .CIN(C[0]), .S(S[1]), .COUT(COUT));
endmodule
module CLA(A,B,CIN,S,COUT); ASSISTANT: 
	input A,B,CIN;
	output S,COUT;
	wire P,G;
	assign P = A ^ B;
	assign G = A & B;
	assign S = P ^ CIN;
	assign COUT = G | (P & CIN);
endmodule

// This is a 16-bit carry select adder
 // It takes two 16-bit inputs, and a carry-in
 // It returns a 16-bit sum, and a carry-out
`timescale 1ns/1ps
module carry_select_adder_16bit(a, b, cin, sum, cout);
	input [15:0] a, b;
	input cin;
	output [15:0] sum;
	output cout;
	wire [3:0] c;
	// Instantiate the first 4-bit carry select adder
	carry_select_adder_4bit csa1(a[3:0], b[3:0], cin, sum[3:0], c[0]);
	// Instantiate the second 4-bit carry select adder
	carry_select_adder_4bit csa2(a[7:4], b[7:4], c[0], sum[7:4], c[1]);
	// Instantiate the third 4-bit carry select adder
	carry_select_adder_4bit csa3(a[11:8], b[11:8], c[1], sum[11:8], c[2]);
	// Instantiate the fourth 4-bit carry select adder
	carry_select_adder_4bit csa4(a[15:12], b[15:12], c[2], sum[15:12], cout);
endmodule
module carry_select_adder_4bit(a, b, cin, sum, cout);
	input [3:0] a, b;
	input cin;
	output [3:0] sum;
	output cout;
	wire [3:0] c;
	// Instantiate the first 4-bit ripple carry adder
	ripple_carry_adder_4bit rca1(a[3:0], b[3:0], cin, sum[3:0], c[0]);
