// This is a 16-bit carry select adder
 // It takes two 16-bit inputs, and a carry-in
 // It returns a 16-bit sum, and a carry-out
 // Carry-in and carry-out are 1-bit
 // This design is based on the one from:
 // http://www.swarth.org/Nick/Projects/V/V_Projects/Verilog_Projects/CarrySelectAdder/
`timescale 1ns/1ps
module carry_select_adder_16bit(a, b, cin, sum, cout);
	input [15:0] a, b;
	input cin;
	output [15:0] sum;
	output cout;
	wire [3:0] c;
	wire [3:0] sum_slice1, sum_slice2, sum_slice3;
	ripple_carry_adder rca1(.a(a[3:0]), .b(b[3:0]), .cin(cin), .sum(sum_slice1), .cout(c[0]));
	ripple_carry_adder rca2(.a(a[7:4]), .b(b[7:4]), .cin(c[0]), .sum(sum_slice2), .cout(c[1]));
	ripple_carry_adder rca3(.a(a[11:8]), .b(b[11:8]), .cin(c[1]), .sum(sum_slice3), .cout(c[2]));
	ripple_carry_adder rca4(.a(a[15:12]), .b(b[15:12]), .cin(c[2]), .sum(sum[15:12]), .cout(c[3]));
	carry_select_adder_slice slice1(.cin(cin), .sum(sum_slice1), .cout(c[0]), .sum_out(sum[3:0]));
	carry_select_adder_slice slice2(.cin(c[0]), .sum(sum_slice2), .cout(c[1]), .sum_out(sum[7:4]));
	carry_select_adder_slice slice3(.cin(c[1]), .sum(sum_slice3), .cout(c[2]), .sum_out(sum[11:8]));
	carry_select_adder_slice slice4(.cin(c[2]), .sum(sum[15:12]), .cout(c[3]), .sum_out(sum[15:12]));
	assign cout = c[3];
endmodule
module carry_select_adder_slice(cin, sum, cout, sum_out);
	input cin, cout;
	input [3:0] sum;
	output [3:0] sum_out;
	wire [3:0] sum_slice1, sum_slice2;
	wire cout_slice1, cout_slice2;
	ripple_carry_adder rca1(.a(sum[1:0]), .b(sum[3:2]), .cin(cin), .sum(sum_slice1), .cout(cout_slice1));
	ripple_carry_adder rca2(.a(sum[3:2]), .b(sum[3:2]), .cin(cin), .sum(sum_slice2), .cout(cout_slice2));
	mux2to1 mux1(.in0(sum_slice1), .in1(sum_slice2), .sel(cout_slice1), .out(sum_out[3:2]));
	mux2to1 mux2(.in0(cout_slice1), .in1(cout_slice2), .sel(cout), .out(cout_slice3));
	mux2to1 mux3(.in0(sum[0]), .in1(sum[1]), .sel(cout_slice3), .out(sum_out[0]));
	mux2to1 mux4(.in0(sum[1]), .in1(sum[1]), .sel(cout), .out(sum_out[1]));
	mux2to1 mux5(.in0(sum[2]), .in1(sum[2]), .sel(cout), .out(sum_out[2]));
	mux2to1 mux6(.in0(sum[3]), .in1(sum[3]), .sel(cout), .out(sum_out[3]));
endmodule
module ripple_carry_adder(a, b, cin, sum, cout);
	input [3:0] a, b;
	input cin;
	output [3:0] sum;
	output cout;
	wire [3:0] c;
	full_adder fa0(.a(a[0]), .b(b[0]), .cin(cin), .sum(sum[0]), .cout(c[0]));
	full_adder fa1(.a(a[1]), .b(b[1]), .cin(c[0]), .sum(sum[1]), .cout(c[1]));
	full_adder fa2(.a(a[2]), .b(b[2]), .cin(c[1]), .sum(sum[2]), .cout(c[2]));
	full_adder fa3(.a(a[3]), .b(b[3]), .cin(c[2]), .sum(sum[3]), .cout(c[3]));
	assign cout = c[3];
endmodule
module full_adder(a, b, cin, sum, cout);
	input a, b, cin;
	output sum, cout;
	assign sum = a ^ b ^ cin;
	assign cout = (a & b) | (b & cin) | (cin & a);
endmodule
module mux2to1(in0, in1, sel, out);
	input in0, in1, sel;
	output out;
	assign out = (in0 & ~sel) | (in1 & sel);
endmodule

// This is a 16-bit carry select adder
 // It takes two 16-bit inputs, and a carry-in
 // It returns a 16-bit sum, and a carry-out
`timescale 1ns / 1ps
module carry_select_adder_16bit(a, b, cin, sum, cout);
    input [15:0] a, b;
    input cin;
    output [15:0] sum;
    output cout;
    wire [3:0] sum0, sum1, sum2, sum3;
    wire cout0, cout1, cout2, cout3;
    // First 4-bit section
    ripple_carry_adder rca0(a[3:0], b[3:0], cin, sum0, cout0);
    // Second 4-bit section
    carry_select_adder_slice csa_slice1(a[7:4], b[7:4], cin, sum1, cout1);
    // Third 4-bit section
    carry_select_adder_slice csa_slice2(a[11:8], b[11:8], cin, sum2, cout2);
    // Fourth 4-bit section
    carry_select_adder_slice csa_slice3(a[15:12], b[15:12], cin, sum3, cout3);
    assign sum = {sum3, sum2, sum1, sum0};
    assign cout = cout3 | cout2 | cout1 | cout0;
endmodule
module carry_select_adder_slice(a, b, cin, sum, cout);
    input [7:0] a, b;
    input cin;
    output [7:0] sum;
    output cout;
    wire [3:0] sum0, sum1;
    wire cout0, cout1;
    // First 4-bit ripple carry adder
    ripple_carry_adder rca0(a[3:0], b[3:0], cin, sum0, cout0);
    // Second 4-bit ripple carry adder
    ripple_carry_adder rca1(a[7:4], b[7:4], cin, sum1, cout1);
    // Multiplexer to select sum and cout
    mux2to1 mux_sum(sum0, sum1, cout0, cout1, sum, cout);
endmodule
module ripple_carry_adder(a, b, cin, sum, cout);
    input [3:0] a, b;
    input cin;
    output [3:0] sum;
    output cout;
    wire [3:0] sum0, sum1, sum2;
    wire cout0, cout1, cout2;
    // First 4-bit adder
    adder_4bit adder0(a[3:0], b[3:0], cin, sum0, cout0);
    // Second 4-bit adder
    adder_4bit adder1(a[3:0], b[3:0], cin, sum1, cout1);
    // Third 4-bit adder
    adder_4bit adder2(a[3:0], b[3:0], cin, sum2, cout2);
    assign sum = {sum2, sum1, sum0};
    assign cout = cout2 | cout1 | cout0;
