module adder_4bit(a, b, cin, sum, cout);
    input [3:0] a, b;
    input cin;
    output [3:0] sum;
    output cout;
    wire [3:0] sum0, sum1;
    wire cout0, cout1;
    // First 4-bit adder
    adder_1bit adder0(a[0], b[0], cin, sum0[0], cout0);
    // Second 4-bit adder
    adder_1bit adder1(a[1], b[1], cin, sum0[1], cout0);
    // Third 4-bit adder
    adder_1bit adder2(a[2], b[2], cin, sum0[2], cout0);
    // Fourth 4-bit adder
    adder_1bit adder3(a[3], b[3], cin, sum0[3], cout0);
    // First 4-bit adder
    adder_1bit adder4(a[0], b[0], cin, sum1[0], cout1);
    // Second 4-bit adder
    adder_1bit adder5(a[1], b[1], cin, sum1[1], cout1);
    // Third 4-bit adder
    adder_1bit adder6(a[2], b[2], cin, sum1[2], cout1);
    // Fourth 4-bit adder
    adder_1bit adder7(a[3], b[3], cin, sum1[3], cout1);
    assign sum = {sum1, sum0};
    assign cout = cout1 | cout0;
endmodule
module adder_1bit(a, b, cin, sum, cout);
    input a, b, cin;
    output sum, cout;
    assign sum = a ^ b ^ cin;
    assign cout = (a & b) | (b & cin) | (cin & a);
