// This is a 16-bit carry select adder
 // It takes two 16-bit inputs, and a carry-in input
 // It returns a 16-bit sum, and a carry-out
 // It is designed to be used in a carry select adder
 // It is built from several 4-bit carry select adder slices
 // It uses a ripple carry adder for the first 4 bits
 // It uses a carry select adder slice for the remaining 12 bits
`timescale 1ns / 1ps
module carry_select_adder_16bit(a, b, cin, sum, cout);
	input [15:0] a, b;
	input cin;
	output [15:0] sum;
	output cout;
	wire [15:0] sum;
	wire cout;
	wire [15:0] c;
	wire [15:0] c_out;
	wire [15:0] c_in;
	assign c_in = {16{cin}};
	// First 4-bit section
	ripple_carry_adder_4bit rca0 (.a(a[3:0]), .b(b[3:0]), .cin(c_in[3:0]), .sum(sum[3:0]), .cout(c[3:0]));
	// First 4-bit carry select adder slice
	carry_select_adder_slice_4bit csa_slice1 (.a(a[7:4]), .b(b[7:4]), .cin(c[3:0]), .sum(sum[7:4]), .cout(c[7:4]));
	// Second 4-bit carry select adder slice
	carry_select_adder_slice_4bit csa_slice2 (.a(a[11:8]), .b(b[11:8]), .cin(c[7:4]), .sum(sum[11:8]), .cout(c[11:4]));
	// Third 4-bit carry select adder slice
	carry_select_adder_slice_4bit csa_slice3 (.a(a[15:12]), .b(b[15:12]), .cin(c[11:4]), .sum(sum[15:12]), .cout(c[15:4]));
	// Carry-out
	assign cout = c[15];
