module carry_select_adder_slice_4bit(a, b, cin, sum, cout);
	input [3:0] a, b;
	input [3:0] cin;
	output [3:0] sum;
	output [3:0] cout;
	wire [3:0] sum;
	wire [3:0] cout;
	wire [3:0] c;
	wire [3:0] c_out;
	wire [3:0] c_in;
	assign c_in = {4{cin}};
	// First 4-bit ripple carry adder
	ripple_carry_adder_4bit rca0 (.a(a[3:0]), .b(b[3:0]), .cin(c_in[3:0]), .sum(sum[3:0]), .cout(c[3:0]));
	// Second 4-bit ripple carry adder
	ripple_carry_adder_4bit rca1 (.a(a[7:4]), .b(b[7:4]), .cin(c[3:0]), .sum(sum[7:4]), .cout(c[7:4]));
	// Carry-out
