// This is a 16-bit carry select adder
 // It takes two 16-bit inputs, and a carry-in input
 // It returns a 16-bit sum, and a carry-out
 // This module is designed to be used in a carry select adder
 // It is built from several 4-bit carry select adder slices
 // Each slice is designed to compute a sum and carry bit based on the previous carry
 // The first 4-bit slice uses a ripple carry adder
 // Subsequent slices use carry select adder slices, which compute sums and carry-out bits based on the previous carry
 // Each slice contains two ripple carry adders, and a multiplexer to select the appropriate sum and carry based on the carry-in
`timescale 1ns / 1ps
module carry_select_adder_16bit(a, b, cin, sum, cout);
	input [15:0] a, b;
	input cin;
	output [15:0] sum;
	output cout;
	wire [15:0] sum;
	wire cout;
	wire [3:0] c;
	wire [3:0] sum_slice1, sum_slice2, sum_slice3;
	wire [3:0] cout_slice1, cout_slice2, cout_slice3;
	// First 4-bit slice
	ripple_carry_adder_4bit rca1 (.a(a[3:0]), .b(b[3:0]), .cin(cin), .sum(sum_slice1), .cout(cout_slice1));
	// Second 4-bit slice
	carry_select_adder_slice csa_slice1 (.a(a[7:4]), .b(b[7:4]), .cin(cout_slice1), .sum(sum_slice2), .cout(cout_slice2));
	// Third 4-bit slice
	carry_select_adder_slice csa_slice2 (.a(a[11:8]), .b(b[11:8]), .cin(cout_slice2), .sum(sum_slice3), .cout(cout_slice3));
	// Fourth 4-bit slice
	carry_select_adder_slice csa_slice3 (.a(a[15:12]), .b(b[15:12]), .cin(cout_slice3), .sum(sum[15:0]), .cout(cout));
endmodule
module carry_select_adder_slice(a, b, cin, sum, cout);
	input [3:0] a, b;
	input cin;
	output [3:0] sum;
	output cout;
	wire [3:0] sum;
	wire cout;
	wire [3:0] c;
	wire [3:0] sum_slice1, sum_slice2;
	wire [3:0] cout_slice1, cout_slice2;
	// First 4-bit slice
	ripple_carry_adder_4bit rca1 (.a(a[3:0]), .b(b[3:0]), .cin(cin), .sum(sum_slice1), .cout(cout_slice1));
	// Second 4-bit slice
	carry_select_adder_slice csa_slice1 (.a(a[7:4]), .b(b[7:4]), .cin(cout_slice1), .sum(sum_slice2), .cout(cout_slice2));
	// Compute sum and carry
	mux_4bit_2to1 mux_sum (.a(sum_slice1), .b(sum_slice2), .sel(cin), .out(sum[3:0]));
	mux_4bit_2to1 mux_cout (.a(cout_slice1), .b(cout_slice2), .sel(cin), .out(cout));
endmodule
