module control (
    input clk,          // Chip clock signal.
    input rst_n,        // Active-low reset signal.
    input start,        // Chip enable signal.
    output reg [4:0] shift_count, // Shift count register.
    output reg load_reg,   // Load register control signal.
    output reg load_prod,  // Load product control signal.
    output reg done        // Chip output flag signal.
);
  // Internal signals declaration
  reg [4:0] shift_count_comb;
  reg load_reg_comb;
  reg load_prod_comb;
  reg done_comb;
  // Sequential logic
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      shift_count <= 5'b0;
      load_reg <= 1'b0;
      load_prod <= 1'b0;
      done <= 1'b0;
    end else begin
      shift_count <= shift_count_comb;
      load_reg <= load_reg_comb;
      load_prod <= load_prod_comb;
      done <= done_comb;
    end
  end
  // Combinational logic
  always @(*) begin
    // Default values
    shift_count_comb = shift_count;
    load_reg_comb = load_reg;
    load_prod_comb = load_prod;
    done_comb = done;
    // Reset condition
    if (!rst_n) begin
      shift_count_comb = 5'b0;
      load_reg_comb = 1'b1;
      load_prod_comb = 1'b0;
      done_comb = 1'b0;
    // Start condition
    end else if (start) begin
      shift_count_comb = 5'b0;
      load_reg_comb = 1'b1;
      load_prod_comb = 1'b1;
      done_comb = 1'b0;
    // Shift count increment
    end else if (shift_count < 5'd16) begin
      shift_count_comb = shift_count + 1'b1;
      load_reg_comb = 1'b0;
      load_prod_comb = 1'b0;
      done_comb = 1'b0;
    // Done condition
    end else begin
      done_comb = 1'b1;
    end
  end
endmodule
