    input [15:0] bin,   // Input b (multiplier) with a data width of 16 bits.
    input [4:0] shift_count, // Shift count register.
    output reg [15:0] mul_out, // Multiplier output with a data width of 16 bits.
    output reg [31:0] prod    // Product register with a data width of 32 bits.
);
  // Internal signals declaration
  reg [15:0] a;
  reg [15:0] b;
  // Sequential logic
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      a <= 16'b0;
      b <= 16'b0;
      mul_out <= 16'b0;
      prod <= 32'b0;
    end else begin
      if (load_reg) begin
        a <= ain;
        b <= bin;
      end
      mul_out <= b << shift_count;
      prod <= {a, mul_out};
    end
  end
