    end else begin
      if (start) begin
        product <= 32'b0;
      end else begin
        if (shift_count > 0 && shift_count < 17) begin
          product <= product + (multiplier << shift_count);
        end
      end
    end
  end
  // Output assignment
  assign yout = product;
  assign done = done_flag;
endmodule
// Multiplier Unit
// ----------------
// Shift-and-accumulate logic
always @(posedge clk) begin
  if (shift_count > 0 && shift_count < 17) begin
    if (multiplicand[15]) begin
      product <= product + (multiplier << shift_count);
    end
  end
end
endmodule

// Internal signals declaration
reg [4:0] shift_count; // Shift count register
reg [15:0] multiplicand; // Multiplicand register
reg [15:0] multiplier; // Multiplier register
reg done_reg; // Chip output flag register
// Control Unit
// Reset: reset_n = 0, Start: start = 1, Shift Count: 16
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        shift_count <= 5'b0;
        done_reg <= 1'b0;
    end
    else if (start) begin
        shift_count <= 5'b0;
        done_reg <= 1'b0;
    end
    else if (shift_count == 5'd16) begin
        shift_count <= shift_count;
        done_reg <= 1'b1;
    end
    else begin
        shift_count <= shift_count + 1'b1;
        done_reg <= done_reg;
    end
end
// Register Unit
// Reset: reset_n = 0, Load: start = 1
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        multiplicand <= 16'b0;
        multiplier <= 16'b0;
    end
    else if (start) begin
        multiplicand <= ain;
        multiplier <= bin;
    end
    else begin
        multiplicand <= multiplicand;
        multiplier <= multiplier;
    end
end
// Multiplier Unit
// Reset: reset_n = 0, Load: start = 1
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        // Reset the product register
        // The multiplier is loaded with the value of the multiplier register
        // The multiplicand is loaded with the value of the multiplicand register
    end
    else if (start) begin
        // The product register is reset to 0
        // The multiplier is loaded with the value of the multiplier register
        // The multiplicand is loaded with the value of the multiplicand register
    end
    else begin
        // The product register is updated by the accumulated multiplier
        // The multiplier is loaded with the value of the multiplier register
        // The multiplicand is loaded with the value of the multiplicand register
    end
end
// Output Assignment
assign yout = {16'b0, multiplier} + {16'b0, product};
assign done = done_reg;
endmodule

//  PARAMETERS
  parameter DATA_WIDTH = 16;
  parameter SHIFT_COUNT_WIDTH = 4;
  //  VARIABLES
  reg [SHIFT_COUNT_WIDTH-1:0] shift_count;
  reg [DATA_WIDTH-1:0] multiplicand;
  reg [DATA_WIDTH-1:0] multiplier;
  reg done_flag;
  //  CONTROL UNIT
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      shift_count <= 0;
      done_flag <= 0;
    end else begin
      if (start) begin
        shift_count <= shift_count + 1;
        done_flag <= 0;
      end else if (shift_count == 15) begin
        shift_count <= 0;
        done_flag <= 1;
      end else begin
        shift_count <= shift_count;
        done_flag <= 0;
      end
    end
  end
  //  REGISTER UNIT
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      multiplicand <= 0;
      multiplier <= 0;
    end else begin
      if (start) begin
        multiplicand <= ain;
        multiplier <= bin;
      end else begin
        multiplicand <= multiplicand;
        multiplier <= multiplier;
      end
    end
  end
  //  MULTIPLIER UNIT
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      multiplier[0] <= 0;
    end else begin
      if (start) begin
        multiplier[0] <= 0;
      end else begin
        if (multiplicand[shift_count]) begin
          multiplier[0] <= multiplier[0] + 1;
        end else begin
          multiplier[0] <= multiplier[0];
        end
      end
    end
  end
  //  OUTPUT ASSIGNMENT
  assign yout = multiplier;
  assign done = done_flag;
endmodule

// Internal signals declaration
  reg [4:0] shift_count; // Shift count register
  reg [15:0] multiplicand; // Multiplicand register
  reg [15:0] multiplier; // Multiplier register
  reg done_reg; // Chip output flag register
  reg [31:0] product; // Product register
  // Control Unit
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      shift_count <= 5'b0;
      done_reg <= 1'b0;
    end else begin
      if (start) begin
        shift_count <= 5'b0;
        done_reg <= 1'b0;
      end else if (shift_count == 5'b11111) begin
        done_reg <= 1'b1;
      end
    end
  end
  // Register Unit
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      multiplicand <= 16'b0;
      multiplier <= 16'b0;
    end else begin
      if (start) begin
        multiplicand <= ain;
        multiplier <= bin;
      end
    end
  end
  // Multiplier Unit
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      product <= 32'b0;
    end else begin
      if (start) begin
        product <= 32'b0;
      end else if (shift_count != 5'b0) begin
        if (multiplicand[shift_count]) begin
          product <= product + multiplier;
        end
      end
    end
  end
  // Output assignment
  assign yout = product;
  assign done = done_reg;
endmodule
